/**
 * Definition for a binary tree node.
 * struct TreeNode {
 *     int val;
 *     TreeNode *left;
 *     TreeNode *right;
 *     TreeNode(int x) : val(x), left(NULL), right(NULL) {}
 * };
 */
class Solution
{
private:
    TreeNode* leftRotate(TreeNode *node)
    {
        auto *pRoot = node->right;
        node->right = pRoot->left;
        pRoot->left = node;
        return pRoot;
    }

    TreeNode* rightRotate(TreeNode *node)
    {
        auto *pRoot = node->left;
        node->left = pRoot->right;
        pRoot->right = node;
        return pRoot;
    }

    int height(TreeNode *node)
    {
        auto const left_h = node->left ? height(node->left) : 0;
        auto const right_h = node->right ? height(node->right) : 0;
        return std::max(left_h, right_h) + 1;
    }

    int bfactor(TreeNode *node)
    {
        return node ? (node->right ? height(node->right) : 0) - (node->left ? height(node->left) : 0) : 0;
    }

    TreeNode* balance(TreeNode *node)
    {
        if (!node)
            return nullptr;
        node->left = balance(node->left);
        node->right = balance(node->right);
        auto const bf = bfactor(node);
        if (bf >= 2)
        {
            if (bfactor(node->right) < 0)
                node->right = rightRotate(node->right);
            return balance(leftRotate(node));
        }
        else if (bf <= -2)
        {
            if (bfactor(node->left) > 0)
                node->left = leftRotate(node->left);
            return balance(rightRotate(node));
        }
        return node;
    }

public:
    TreeNode* balanceBST(TreeNode* root) {
        return balance(root);
    }
};
