 *     TreeNode left;
 *     TreeNode right;
 *     TreeNode() {}
 *     TreeNode(int val) { this.val = val; }
 *     TreeNode(int val, TreeNode left, TreeNode right) {
 *         this.val = val;
 *         this.left = left;
 *         this.right = right;
 *     }
 * }
 */
class Solution {
    public void traversal(TreeNode root, List<Integer> list) {

        // Return if the current node is null
        //base case
        if(root == null) return;
        // Recursively traverse the left and right subtrees
        traversal(root.left,list);
        // Add the current node's value to the result list
        list.add(root.val);
        traversal(root.right, list);
   }
   public TreeNode balance(ArrayList<Integer> nums, int start, int end){
       if(start>end){
           return null;
       }
       int mid = (start+end)/2;
       TreeNode root = new TreeNode(nums.get(mid));
       root.left = balance(nums,start,mid-1);
       root.right = balance(nums, mid+1,end);
       return root;
   }
    public TreeNode balanceBST(TreeNode root) {
        ArrayList<Integer> list = new ArrayList<>();
        if(root == null){
            return null;
        }
        traversal(root, list);
        TreeNode newRoot = balance(list,0,list.size()-1);
        return newRoot;
    }
}
