class Solution:
	def balanceBST(self, root: TreeNode) -> TreeNode:
		nodes = []

		def inOrderTraverse(node: TreeNode):
			if not node.right and not node.left:
				nodes.append(node.val)
				return

			if node.left:
				inOrderTraverse(node.left)

			nodes.append(node.val)

			if node.right:
				inOrderTraverse(node.right)

		def divideAndConquer(nodes_list: List[int], l: int, r: int) -> TreeNode:
			if l >= 0 and r < len(nodes_list) and l <= r:
				mid = (l + r) // 2
				mid_val = nodes_list[mid]

				new_node = TreeNode(mid_val)

				new_node.left = divideAndConquer(nodes_list, l, mid - 1)
				new_node.right = divideAndConquer(nodes_list, mid + 1, r)

				return new_node

		inOrderTraverse(root)
		return divideAndConquer(nodes, 0, len(nodes)-1)

