/**
 * Definition for a binary tree node.
 * struct TreeNode {
 *     int val;
 *     TreeNode *left;
 *     TreeNode *right;
 *     TreeNode() : val(0), left(nullptr), right(nullptr) {}
 *     TreeNode(int x) : val(x), left(nullptr), right(nullptr) {}
 *     TreeNode(int x, TreeNode *left, TreeNode *right) : val(x), left(left), right(right) {}
 * };
 */
class Solution {
public:
    void dfs(TreeNode* node, vector<int>& v){
        if(node == NULL) return;
        dfs(node->left,v);
        v.push_back(node->val);
        dfs(node->right,v);
    }

    TreeNode* createTree(vector<int>& v, int i, int j){
        if(i>j) return NULL;
        int mid = (i+j)/2;
        TreeNode* cur = new TreeNode(v[mid]);
        cur->left = createTree(v,i,mid-1);
        cur->right = createTree(v,mid+1,j);
        return cur;
    }

    TreeNode* balanceBST(TreeNode* root) {
        vector<int> v;
        dfs(root,v);
        TreeNode* res = createTree(v,0,v.size()-1);
        return res;
    }
};
