    private TreeNode BuildBST(List<int> list,int l,int r){
		//This is base case, you can't build a tree at this point so return null
        if(l>r){return null;}

        int mid=l+(r-l)/2;
		//make the middle element root
        TreeNode root=new TreeNode(list[mid]);
		//from left end to mid-1 will be left sub-tree
        root.left=BuildBST(list,l,mid-1);
		//from mid+1 to right end will be right sub tree
        root.right=BuildBST(list,mid+1,r);

        return root;
    }

	// Do in-order traversal and convert to list
    private List<int> InorderTraverse(TreeNode root,List<int> list){
        if(root==null){
            return list;
        }
        InorderTraverse(root.left,list);
        list.Add(root.val);
        InorderTraverse(root.right,list);
        return list;
    }
}
