class Solution {

    private void inorder(TreeNode root, List<TreeNode> nodes) {
        if (root != null) {
            inorder(root.left, nodes);
            root.left = null;
            nodes.add(root);
            inorder(root.right, nodes);
            root.right = null;
        }
    }

    private TreeNode create(List<TreeNode> nodes, int start, int end) {
        if (start > end) return null;
        if (start == end) {
            return nodes.get(start);
        }
        int mid = start + (end - start) / 2;
        nodes.get(mid).left = create(nodes, start, mid-1);
        nodes.get(mid).right = create(nodes, mid+1, end);
        return nodes.get(mid);
    }

    public TreeNode balanceBST(TreeNode root) {
        List<TreeNode> nodes = new ArrayList<>();
        inorder(root, nodes);
        return create(nodes, 0, nodes.size() - 1);
    }
}
