class Solution {

    public TreeNode balanceBST(TreeNode root) {
        if (root == null) return null;

        TreeNode parent = new TreeNode(0);
        parent.left = root;
        balance(parent.left, parent);
        return parent.left;
    }

    private void balance(TreeNode node, TreeNode parent) {
        if (node == null) return;
        balance(node.left, node); 
        balance(node.right, node);

        int bal = getBalance(node);
        if (bal > 1) {
            if (getBalance(node.left) < 0) {
                rotateLeft(node.left, node); // LR
            }
            balance(rotateRight(node, parent), parent); // LL
        } else if (bal < -1) {
            if (getBalance(node.right) > 0) {
                rotateRight(node.right, node); // RL
            }
            balance(rotateLeft(node, parent), parent); // RR
        }
    }

    private TreeNode rotateLeft(TreeNode x, TreeNode parent) {
        TreeNode y = x.right;
        x.right = y.left;
        y.left = x;

        if (parent.left == x) parent.left = y;
        if (parent.right == x) parent.right = y;

        return y;
    }

    private TreeNode rotateRight(TreeNode x, TreeNode parent) {
        TreeNode y = x.left;
        x.left = y.right;
        y.right = x;

        if (parent.left == x) parent.left = y;
        if (parent.right == x) parent.right = y;

        return y;
    }

    private int getBalance(TreeNode node) {
        if (node == null) return 0;
        int l = getHeight(node.left);
        int r = getHeight(node.right);
        return l-r;
    }

    private int getHeight(TreeNode node) {
        if (node == null) return -1;
        int l = getHeight(node.left);
        int r = getHeight(node.right);
        return 1 + Math.max(l, r);
    }
}

