public:

    TreeNode* tobbst(vector<int>& v, int l, int r) {
        if(l>r) {
            return NULL;
        }
        int mid = (l+r)/2;
        TreeNode* root = new TreeNode(v[mid]);
        root->left = tobbst( v, l, mid-1);
        root->right = tobbst( v, mid+1, r);
        return root;
    }

    void inorder(TreeNode* root, vector<int>& v) {
        if(root == NULL) {
            return;
        }
        inorder(root->left, v);
        v.push_back(root->val);
        inorder(root->right, v);
    }

    TreeNode* balanceBST(TreeNode* root) {
        vector<int> v;
        inorder(root, v);
        return tobbst(v, 0, v.size()-1);
    }
};
