    public void inorder(TreeNode root, List<Integer>l) {
        if(root == null) {return ;}
        inorder(root.left, l);
        l.add(root.val);
        inorder(root.right, l);
    }
    public TreeNode balanceBST(TreeNode root) {
        List<Integer> l = new ArrayList<>();
        inorder(root, l);
        return solve(l, 0, l.size()-1);
    }
}
