class Solution {
public:
    TreeNode* balanceBST(TreeNode* root) {
        vector<int> elems;
        collect(root, elems);
        return build(elems.cbegin(), elems.cend());
    }

    void collect(TreeNode *root, vector<int> &elems)
    {
        if (!root) return;
        collect(root->left, elems);
        elems.push_back(root->val);
        collect(root->right, elems);
    }

    TreeNode *build(vector<int>::const_iterator s, vector<int>::const_iterator e)
    {
        if (s >= e) return nullptr;
        const auto mid = s + (e - s) / 2;
        return new TreeNode(*mid, build(s, mid), build(next(mid), e));
    }
};
