class Solution {
    vector<TreeNode*> inOrder;
    void inorderTraverse(TreeNode* root) {
        if( root == nullptr)
            return; 
       inorderTraverse(root->left);

       inOrder.push_back(root);
       inorderTraverse(root->right); 
       root->left = root->right = nullptr;
    }


    TreeNode* formBalancedBinaryTree(int start , int end) {
        if( start > end)
            return nullptr;
        else if(start == end)
            return inOrder[start];
        else {
            int mid = (start + end)/2;
            inOrder[mid]->left = formBalancedBinaryTree(start, mid-1);
            inOrder[mid]->right = formBalancedBinaryTree(mid+1, end);
            return inOrder[mid];
        }
    }
public:
    TreeNode* balanceBST(TreeNode* root) {
      inorderTraverse(root); 
      return formBalancedBinaryTree(0, inOrder.size()-1);
    }
};
