    void inorder(TreeNode* root, vector<TreeNode*> &in){
        if(!root)
        return ;

        inorder(root->left, in);
        in.push_back(root);
        inorder(root->right, in);
    }

    TreeNode* balancing(int start, int end, vector<TreeNode*> &in){
        if(start>end) return NULL;

        int mid = (start+end)/2;
        in[mid]->left = balancing(start, mid-1, in);
        in[mid]->right = balancing(mid+1, end, in);
        return in[mid];
    }

    TreeNode* balanceBST(TreeNode* root) {
        vector<TreeNode*> in;
        inorder(root, in);
        return balancing(0,in.size()-1, in);
    }
};

/**
 * Definition for a binary tree node.
 * public class TreeNode {
 *     int val;
 *     TreeNode left;
 *     TreeNode right;
 *     TreeNode() {}
 *     TreeNode(int val) { this.val = val; }
 *     TreeNode(int val, TreeNode left, TreeNode right) {
 *         this.val = val;
 *         this.left = left;
 *         this.right = right;
 *     }
 * }
 */
class Solution {
    public TreeNode balanceBST(TreeNode root) {
        ArrayList<Integer> list=new ArrayList<>();
        ino(root,list);
int e=list.size()-1;
      return  bs(list,0,e);
    }

    public void ino(TreeNode root, ArrayList<Integer> list){
        if(root==null)return;
ino(root.left,list);
list.add(root.val);
ino(root.right,list);
    }

    public TreeNode bs(ArrayList<Integer> list,int s,int e){
        if(s>e) return null;
        int mid=s+(e-s)/2;
        TreeNode node=new TreeNode(list.get(mid));
        node.left=bs(list,s,mid-1);
        node.right=bs(list,mid+1,e);
        return node;
    }
}

