class Solution {
    public String longestCommonPrefix(String[] strs) {
        if(strs.length == 0){
            return "";
        }
        String prefix = strs[0];
        for(String s : strs){
            int len = Math.min(prefix.length(), s.length());
            for(int i = len; i >= 0; i--){
                if(prefix.indexOf(s.substring(0, i)) == 0){
                    prefix = s.substring(0, i);
                    break;
                }
            }
        }
        return prefix;
    }
}
