    TreeMap<Character, Node> children;
    boolean endOfWord;

    Node() {
        children = new TreeMap<>();
        endOfWord = false;
    }
}

class Solution {
    Node root;

	// insert the characters in trie
    public void insert(String word) {
        Node curr = this.root;
        for(int i=0;i<word.length();i++) {
            char ch = word.charAt(i);
            if( curr.children.get(ch) == null ) {
                curr.children.put(ch, new Node());
            }
            curr = curr.children.get(ch);
        }
        curr.endOfWord = true;
    }

	// Function : find the longest common prefix
    public String lcp() {

        StringBuilder result = new StringBuilder();
        Node curr = this.root;
        while(curr!=null && !curr.endOfWord && curr.children.size()==1) {

            // iterate the map and keep on appending the Character with the answer
			// much simpler iterator below 
            Iterator<Map.Entry<Character, Node>> treemapItertor = curr.children.entrySet().iterator();

			if(treemapItertor.hasNext()) {
                Map.Entry<Character, Node> entry = treemapItertor.next();
				result.append(entry.getKey());
				curr = entry.getValue();
            }
        }

		/*  This can also be done like below : 
				while(curr!=null && !curr.isEndOfWord && curr.children.size() == 1) {
					for(Character key : curr.children.keySet()) {
						 result.append(key);
						curr = curr.children.get(key);
						break;  // break out this for loop as we want only the single character
					}
				}

		*/
        return result.toString();
    }
    public String longestCommonPrefix(String[] strs) {
        Solution trieObj = new Solution();
        trieObj.root = new Node();

        if(strs==null || strs.length==0) {
            return new String("");
        }
		// insert the words in Trie
        for(String s : strs) {
            trieObj.insert(s);
        }
		// find the LCP !
        return trieObj.lcp();
    }
}
