class Solution(object):
    def longestCommonPrefix(self, strs):
        """
        :type strs: List[str]
        :rtype: str
        """
        MinW = min(strs)
        MaxW = max(strs)
        left,right = 0 , len(min(strs))
        while left < right:
            mid = (left+right)//2
            if MaxW[mid]==MinW[mid]:
                left = mid+1
            else: 
                right = mid-1
        if strs[0][left]==strs[1][left]:
            return strs[0][:left]
        else:
            return ""

