        string s1;
        for(int i=1;i<strs.size();i++)
        {
            if(strs[i]=="\0")
            {
                return "\0";
            }
            while(s[j]==strs[i][j])
            {
                s1=s1+s[j];
                j++;
            }
            s=s1;
            s1.clear();
            j=0;

        }
        string f;
        int i=0;
        return f;

    }
};

