`public class Solution
{
    public string FindShortestWord(string[] strs)
    {
        var shortestWord = strs[0];

        foreach (var word in strs)
        {
            if (word.Length < shortestWord.Length)
                shortestWord = word;
        }

        return shortestWord;
    }

    public bool SymbolIsContainedInStringsOnPosition(char symbol, string[] strings, int position)
    {
        for (int i = 0; i < strings.Length; i++)
        {
            if (strings[i][position] != symbol)
                return false;
        }

        return true;
    }

    public string LongestCommonPrefix(string[] strs)
    {
        var shortestWord = FindShortestWord(strs);
        var answer = "";

        for (int i = 0; i < shortestWord.Length; i++)
        {
            if (SymbolIsContainedInStringsOnPosition(shortestWord[i], strs, i))
                answer += shortestWord[i];
            else
                break;
        }

        return answer;
    }
}`

