class Solution { // Time: O(N), Space: O(1)
public:
	int firstMissingPositive(vector<int>& nums) {
		int size = nums.size();
		bool hasOne = false;

		for (int& n : nums) { // Set all negatives to 0
			if (n < 0)
				n = 0;
			else if (n == 1)
				hasOne = true;
		}

		if (!hasOne) // One is the first missing
			return 1;

		for (int i = 0; i < size; i++) { // Negate all numbers at indices by found values
            int currNum = abs(nums[i]);
			if (currNum > 0 && currNum <= size)
				if (nums[currNum - 1] == 0)
					nums[currNum - 1] = -1;
				else if (nums[currNum - 1] > 0)
					nums[currNum - 1] *= -1; // Negate it
		}

		for (int i = 0; i < size; i++) // Find first non negative number
			if (nums[i] > -1)
				return i + 1;

		return size + 1;
	}
};
```

