class Solution:
    def swap(self, arr, a, b):
        tmp = arr[a]
        arr[a] = arr[b]
        arr[b] = tmp
        return arr

    def firstMissingPositive(self, nums: List[int]) -> int:
        n = len(nums)
        for i in range(n):
            while 1 <= nums[i] <= n and nums[i] != nums[nums[i]-1]:
                nums = self.swap(nums,i,nums[i]-1)
        for j in range(n):
            if nums[j] != j+1:
                return j+1
        return n+1
```

