public:
    int firstMissingPositive(vector<int>& nums) {
        int mn=INT_MAX;
        int n=nums.size();

        for(int i=0; i<n; i++){
            if(nums[i]>0 && mn>nums[i])
                mn = nums[i];
            if(nums[i]<1 || nums[i]>n) 
                nums[i]=1;
