class Solution {
public:
    int firstMissingPositive(vector<int>& v) {
        //erase all the elements having negative and zero value.
       for(int i=0;i<v.size();i++){
           if(v[i]<=0){
               v.erase(v.begin()+i);
               i--;
           }
       }
       sort(v.begin(),v.end()); //sort the array.
       //Now,remove all duplicates.This approach works in O(n) time
       for(int i=0;i<v.size();i++){
            int com=v[i];
            int freq=0;
            while(i<v.size() && v[i]==com){
                freq++;
                if(freq>1){
                 v.erase(v.begin()+i);
                 i--;
                }
                i++;
            }
            i--;
        }
        for(int i=0;i<v.size();i++){
            if(v[i]!=i+1){
                return i+1;
            }
        }
        int ans=v.size()+1;
        return ans;
    }
};
```

