```
class Solution {
public:

    int firstMissingPositive(vector<int>& nums) 
    {
        int n=nums.size();
        vector<bool>res(n+1, 0);

        for(int i=0;i<n;i++)
        {
            if(nums[i] > 0 and nums[i] <= n) // positive number inside the range 0 to n
            {
                res[nums[i]] = true;
            }   
        }

        // Indices start from 0 to n-1
        //  Number start from 1 to n
        for(int i=1;i<res.size();i++)
        {
            if(!res[i])  // first positive number that is false
                return i;
        }
        return (n+1);
    }
};
