        //marking the exisitng numbers
        for (int i = 0; i < n; i++){
            if (nums[i] > 0 && nums[i] <= n) {
                // numbers are from 1 to ..
                // indexes to mark are from 0
                // so mark number i at i-1 
                // e.g mark 5 at index 4
                visited[i-1] = true;
            }
        }

        // cheking what was not present
        for(int i = 0; i < n; i++) {
            if(!visited[i]) 
                return i+1;
        }

        // everything was present
