/**
 * Definition for singly-linked list.
 * struct ListNode {
 *     int val;
 *     ListNode *next;
 *     ListNode(int x) : val(x), next(NULL) {}
 * };
 */
class Solution {
public:
    bool hasCycle(ListNode *head) {
        ListNode* slow = head; // making slow pointer
        ListNode* fast = head; // making fast pointer

        while(fast != NULL && fast -> next != NULL)
        {
            slow = slow -> next; // move slow one step at a time
            fast = fast -> next -> next; // move fast two step at a time

            // if at any point slow and fast pointer reaches to the same pos
            // then we can say that yess!, their is a cycle and hence return true

            if(slow == fast) // if slow is equal to fast
            {
                return true; // means cycle, and hence return true
            }
        }

        // otherwise no cycle is present, hence return false
        return false;
    }
};
```
