class Solution {
    int solve(vector<int>& days, vector<int>& costs, int i, int validity, vector<vector<int>> &dp) {
        if(i >= days.size())
            return 0;
        if(dp[i][validity] != -1)
            return dp[i][validity];
        if(days[i] <= validity)
            return dp[i][validity] = solve(days, costs, i+1, validity, dp);
        else {
            int ch1 = costs[0] + solve(days, costs, i+1, days[i], dp);
            int ch2 = costs[1] + solve(days, costs, i+1, days[i]+6, dp);
            int ch3 = costs[2] + solve(days, costs, i+1, days[i]+29, dp);
            return dp[i][validity] = min(ch1, min(ch2, ch3));
        }
    }
public:
    int mincostTickets(vector<int>& days, vector<int>& costs) {
        int n = days.size();
        int max_validity = days[n-1] + 30;
        vector<vector<int>> dp(n, vector<int>(max_validity, -1));
        return solve(days, costs, 0, 0, dp);
    }
};
