// If you DO traverl on day i, dp[i] = min(dp[i-1] + costs[0], dp[i-7] + costs[1] + dp[i-30] + costs[2]).
int forward(vector<int>& days, vector<int>& costs) {
	unordered_set<int> dayset(days.begin(), days.end());

	vector<int> dp(366, 0);
	for(int i = 1; i < 366; ++i) {
		if (dayset.find(i) == dayset.end()) {
			dp[i] = dp[i-1];
		} else {
			dp[i] = min({dp[i-1] + costs[0], 
						 dp[max(i-7, 0)] + costs[1],
						 dp[max(i-30, 0)] + costs[2]});
		}
	}

	return dp[365];
}

// 2. backward
// dp[i] represents the cost you need if day i is 
// the first day in your travel plan. In other words,
// the cost if you travel from day i to day 365.
//
// If you do not travel on day i, dp[i] = dp[i+1];
// If you DO traverl on day i, dp[i] = min(dp[i+1] + costs[0], dp[i+7] + costs[1] + dp[i+30] + costs[2]).
int backward(vector<int>&days, vector<int>& costs) {
	unordered_set<int> dayset(days.begin(), days.end());

	vector<int> dp(367, 0);
	for(int i = 365; i >= 1; --i) {
		if (dayset.find(i) == dayset.end()) {
			dp[i] = dp[i+1];
		} else {
			dp[i] = min({dp[i+1] + costs[0], 
						 dp[min(i+7, 366)] + costs[1],
						 dp[min(i+30, 366)] + costs[2]});
		}
	}

	return dp[1];
}
