class Solution {
public:
    int dp[366];
    int helper(vector<int> &arr,vector<int>& cost,int l)
    {
        if(l>365){
            return 0;
        }

        if(dp[l] != -1){                      // if calculated earlier , then return 
            return dp[l];
        }
        if(arr[l] == 0){                    // if we dont have to travel on lth day , then skip and increase l
            return dp[l] = helper(arr,cost,l+1);          // calculate and store
        }
        else{

            int op1 = cost[0] + helper(arr,cost,l+1);//buy one day ticket
            int op2 = cost[1] + helper(arr,cost,l+7);//buy seven day ticket
            int op3 = cost[2] + helper(arr,cost,l+30);//buy thirty day day ticket


            return dp[l] = min(op1,min(op2,op3));   // return minimum out of three options
        }


    }
    int mincostTickets(vector<int>& days, vector<int>& costs) {


        int n = days.size();
        vector<int> arr(366,0);

        for(int i=0;i<n;i++){
            arr[days[i]]++;
        }

        for(int i=0;i<366;i++) dp[i] = -1;

        return helper(arr,costs,1);
    }
};
```

