private int minCost(int[] days, int[] costs, int i) {
    if (i == days.length) {
        return 0;
    }
    int cost1 = costs[0] + minCost(days, costs, i + 1);
    int j = i;
    while (j < days.length && days[j] < days[i] + 7) {
        j++;
    }
    int cost2 = costs[1] + minCost(days, costs, j);
    j = i;
    while (j < days.length && days[j] < days[i] + 30) {
        j++;
    }
    int cost3 = costs[2] + minCost(days, costs, j);
    return Math.min(cost1, Math.min(cost2, cost3));
}
