class Solution:
    def mincostTickets(self, days: List[int], costs: List[int]) -> int:
        dp = [0] * len(days)
        durations = [1, 7, 30]

        for i in range(len(days)):
            minCost = float('inf')
            curDay = days[i]

            for j in range(len(costs)):
                duration = durations[j]
                cost = costs[j]

                prevIdx = self.getPrevDayIdx(days, i - 1, curDay - duration)
                prevCost = dp[prevIdx] if prevIdx >= 0 else 0

                minCost = min(minCost, prevCost + cost)

            dp[i] = minCost

        return dp[-1]

    def getPrevDayIdx(self, days: List[int], r: int, target: int) -> int:
        l = 0
        idx = -1

        while l <= r:
            m = l + (r - l) // 2

            if days[m] == target:
                return m

            if days[m] < target:
                idx = m
                l = m + 1
            else:
                r = m - 1

        return idx
