def mincostTickets(self, days: List[int], costs: List[int]) -> int:
    # Initialize the DP array with all 0s
    dp = [0] * (days[-1] + 1)

    # Convert the list of travel days into a set for faster lookups
    travel_days = set(days)

    # Compute the minimum cost for each day from 1 to the maximum day
    for i in range(1, len(dp)):
        if i in travel_days:
            # If it's a travel day, try all three types of passes and choose the minimum cost
            cost1 = dp[max(i - 1, 0)] + costs[0]
            cost2 = dp[max(i - 7, 0)] + costs[1]
            cost3 = dp[max(i - 30, 0)] + costs[2]
            dp[i] = min(cost1, cost2, cost3)
        else:
            # If it's not a travel day, the cost is the same as the previous day
            dp[i] = dp[i - 1]

    # Return the minimum cost for the entire travel period, which is the last element in the DP array
    return dp[-1]
