public int minCost(int[] days, int[] costs, int[] pass, int index, int[] dp) {
    // Base case: all days have been covered
    if (index == days.length) {
        return 0;
    }

    // Check if the result is already computed
    if (dp[index] != -1) {
        return dp[index];
    }

    int minCost = Integer.MAX_VALUE;

    // Try all three types of passes
    for (int i = 0; i < costs.length; i++) {
        int cost = costs[i];
        int duration = pass[i];

        // Find the next day after the pass expires
        int j = index;
        while (j < days.length && days[j] < days[index] + duration) {
            j++;
        }

        // Compute the cost for the remaining days
        int remainingCost = cost + minCost(days, costs, pass, j, dp);

        // Update the minimum cost
        minCost = Math.min(minCost, remainingCost);
    }

    // Store the result in the memoization array and return it
    dp[index] = minCost;
    return minCost;
}
