class Solution {
public:
    int mincostTickets(vector<int>& days, vector<int>& costs) {
        vector<int> daypasses({1, 7, 30});
        vector<int> dp(days.size());

        for (int i = 0; i < days.size(); i++) {
            int date = days[i];
            int mincost = INT_MAX;

            for (int j = 0; j < costs.size(); j++) {
                int daypass = daypasses[j];
                int cost = costs[j];

                int preidx = getEqualOrSmaller(days, i - 1, date - daypass);
                int precost = 0;

                if (preidx >= 0) {
                    precost = dp[preidx];
                }

                mincost = min(mincost, precost + cost);
            }

            dp[i] = mincost;
        }

        return dp.back();
    }

    int getEqualOrSmaller(vector<int>& days, int r, int target) {
        int l = 0;
        int idx = -1;

        while (l <= r) {
            int m = l + (r - l) / 2;

            if (days[m] == target) {
                return m;
            }

            if (days[m] < target) {
                idx = m;
                l = m + 1;
            } else {
                r = m - 1;
            }
        }

        return idx;
    }
};
