class Solution {
    public int mincostTickets(int[] days, int[] costs) {
        int[] dp = new int[days.length];
        int[] durations = new int[]{1, 7, 30};

        for (int i = 0; i < days.length; i++) {
            int minCost = Integer.MAX_VALUE;
            int curDay = days[i];

            for (int j = 0; j < costs.length; j++) {
                int duration = durations[j];
                int cost = costs[j];

                int prevIdx = getPrevDayIdx(days, i - 1, curDay - duration);
                int prevCost = prevIdx >= 0 ? dp[prevIdx] : 0;

                minCost = Math.min(minCost, prevCost + cost);
            }

            dp[i] = minCost;
        }

        return dp[dp.length - 1];
    }

    private int getPrevDayIdx(int[] days, int r, int target) {
        int l = 0;
        int idx = -1;

        while (l <= r) {
            int m = l + (r - l) / 2;

            if (days[m] == target) {
                return m;
            }

            if (days[m] < target) {
                idx = m;
                l = m + 1;
            } else {
                r = m - 1;
            }
        }

        return idx;
    }
