class Solution {
    Integer[] memo;
    //memo[i] is the minimum cost from days[i] to days[days.length - 1];
    public int mincostTickets(int[] days, int[] costs) {
        if (days == null || days.length == 0) return 0;
        int n = days.length;
        memo = new Integer[n];      
        return minCost(days, costs, 0);
    }

    private int minCost(int[] days, int[] costs, int lo) {
        int n = days.length;
        if (lo >= n) return 0;
        if (memo[lo] != null) return memo[lo];
        int res = costs[0] + minCost(days, costs, lo + 1);

        //use binary search to find the right most index that has days[i] < days[lo] + 7...
        int index7 = binarySearch(days, lo, days[lo] + 7);
        res = Math.min(res, costs[1] + minCost(days, costs, index7 + 1));

        //use binary search to find the right most index that has days[i] <= days[lo] + 30...
        int index30 = binarySearch(days, lo, days[lo] + 30);
        res = Math.min(res, costs[2] + minCost(days, costs, index30 + 1));

        memo[lo] = res;
        return res;
    }

    private int binarySearch(int[] days, int lo, int target) {
        int hi = days.length - 1;
        while (lo < hi) {
            int mid = lo + (hi - lo + 1) / 2;
            if (days[mid] < target) {
                lo = mid;
            } else {
                hi = mid - 1;
            }
        }
        return lo;
    }
}

