class Solution {
public:
    int mincostTickets(vector<int>& days, vector<int>& costs) {
     int n=days.size();
     int dp[n];
     memset(dp, 0, sizeof(dp));

    for(int i=0; i<n;i++){

        if(i==0){
            //Base Case  Only One Day is there means size of days array is 1.
            dp[i]= *min_element(costs.begin(), costs.end());
            continue;
        }
        // val varible is used  to find minimum cost to reach days[i].
        // Choice one :- (1 day ticket).
        int val= dp[i-1]+costs[0];
        int index= lower_bound(days.begin() ,days.begin()+i, days[i]-6)-days.begin();
        // choice two :- 7 day ticket. As we know days array is sorted in Nature . 
        // So Binary Search we can apply to find started day to Buy a ticket of 7 day pass.
        if(index!=0){
           val=min(dp[index-1]+ costs[1], val); 
        }else{
            val=min(val, costs[1]);
        }
        index=lower_bound(days.begin() ,days.begin()+i, days[i]-29)-days.begin();
        // Similarly with 30 day pass as we do as 7 day pass.
        if(index!=0){
            val= min(dp[index-1]+costs[2], val);
        }else{
            val=min(val, costs[2]);
        }
        // Store val in dp for the Memoization.
        dp[i]=val;
        // cout<<dp[i]<<endl;

    }

    return dp[n-1];


    }
};
