class Solution:
    def mincostTickets(self, days: List[int], costs: List[int]) -> int:
        n = days[-1] + 1
        cache = [0] * n

        # convert days to set for faster lookup
        travel_days = {day for day in days}

        for i in range(1, n):
            if i in travel_days:
                # use max() to pick cache[0] incase `i-day` is out of bounds
                cache[i] = min(
                    [
                        cache[max(i - 1, 0)] + costs[0],  # one day pass
                        cache[max(i - 7, 0)] + costs[1],  # seven day pass
                        cache[max(i - 30, 0)] + costs[2],  # thirty day pass
                    ]
                )
            else:
                # not a travel day
                cache[i] = cache[i - 1]

        return cache[-1]

