class Solution {
public:
    int mincostTickets(vector<int>& days, vector<int>& costs) {
        unordered_set<int> st(begin(days), end(days));

        int last_day = days.back();

        vector<int> t(last_day+1, 0);
        //t[i] will give us min cost to travel till day i.

        t[0] = 0;

        //Start from 1st day & travel till last day
        for(int i=1; i<=last_day; i++)
        {
            /* If i'th day is not found then
            set previous value in current index & continue. */
            if(st.find(i) == st.end())
            {
                t[i] = t[i-1];
                continue;
            }

            //Calculate cost of day 1 pass
            int day_1_pass = costs[0] + t[max(i-1, 0)];

            //Calculate cost of day 7 pass
            int day_7_pass = costs[1] + t[max(i-7, 0)];

            //Calculate cost of day 30 pass
            int day_30_pass = costs[2] + t[max(i-30, 0)];


            //Find minimum of all & store in i'th index.
            t[i] = min({day_1_pass, day_7_pass, day_30_pass});
        }

<!-- ✅Well before returning answer don't forget to UPVOTE.✅ -->
        return t[last_day];
    }
};
