class Solution:
    def mincostTickets(self, days: List[int], costs: List[int]) -> int:
        self.costs = costs
        self.memo = {}
        self.memo[0] = 0
        self.travel = set(days)

        return self.min_ticket(days[-1])

    def min_ticket(self, day):
        if day <= 0:
            day = 0

        if day in self.memo:
            return self.memo[day]

        if day in self.travel:
            self.memo[day] = min(self.min_ticket(day - 1) + self.costs[0], self.min_ticket(day - 7) + self.costs[1], self.min_ticket(day - 30) + self.costs[2])
        else:
            self.memo[day] = self.min_ticket(day - 1)

        return self.memo[day]
