class Solution {
public:
/*Approach One*/
    int solve(int idx,vector<int>& days, vector<int>& costs,vector<int>& dp){
        if(idx >= days.size())
            return 0;

        if(dp[idx] != -1)
            return dp[idx];

        int ans = INT_MAX;
        //1 day pass
        ans = min(ans,costs[0] + solve(idx+1,days,costs,dp));

        int i;

        //7-day pass
        for(i=idx;i<days.size() && days[i]<days[idx]+7;i++);
        ans = min(ans,costs[1]+solve(i,days,costs,dp));

        //30-days pass
        for(i=idx;i<days.size() && days[i]<days[idx]+30;i++);
        ans = min(ans,costs[2]+solve(i,days,costs,dp));

        return dp[idx] = ans;
    }
/*Approach 2*/
    int solveTab(vector<int>& days, vector<int>& costs){
        int n = days.size();
        vector<int> dp(days.size()+1,0);

        for(int idx=n-1;idx>=0;idx--){

            int ans = INT_MAX;

            //1 day pass
            ans = min(ans,costs[0] + dp[idx+1]);

            int i;

            //7-day pass
            for(i=idx;i<days.size() && days[i]<days[idx]+7;i++);
            ans = min(ans,costs[1]+dp[i]);

            //30-days pass
            for(i=idx;i<days.size() && days[i]<days[idx]+30;i++);
            ans = min(ans,costs[2]+dp[i]);

            dp[idx] = ans;
        }
        return dp[0];
    }
    int mincostTickets(vector<int>& days, vector<int>& costs) {

/*Approach 1*/
        // vector<int> dp(days.size(),-1);
        // return solve(0,days,costs,dp);

/*Approach 2*/
        return solveTab(days,costs);
    }
};
