        ans1 += costs[0] + helper(days,costs,i);
        // // 7-days pass
        i = lower_bound(days.begin(), days.end(), days[idx]+7)-days.begin();
        ans2 += costs[1] + helper(days,costs,i);
        // // 30-days pass
        i = lower_bound(days.begin(), days.end(), days[idx]+30)-days.begin();
        ans3 += costs[2] + helper(days,costs,i);

        return dp[idx] = min({ans1,ans2,ans3});
    }

    int mincostTickets(vector<int>& days, vector<int>& costs) {
        memset(dp,-1,sizeof(dp));
        return helper(days,costs,0);
    }
