class Solution {
    public int mincostTickets(int[] days, int[] costs) {
        // using dfs as it is a decision tree
        // at each stage I can choose either 1/7/30 day pass
        // caching already visited indices
        return dfs(days, costs, 0, new int[days.length]);
    }

    private int dfs(int[] days, int[] costs, int index, int[] dp) {

        if(index >= days.length) {
            return 0;
        }

        // cached
        if(dp[index] > 0) {
            return dp[index];
        }


        // 1 Day Pass
        int cost1DayPass = costs[0] + dfs(days, costs, index+1, dp);

        // 7 Days Pass

        // Find next travel day when pass expires
        int i = index;

        while(i < days.length && days[i] < days[index] + 7) {
            i++;
        }

        int cost7DayPass = costs[1] + dfs(days, costs, i, dp);

        // 30 Days Pass

        // Find next travel day when pass expires
        i = index;

        while(i < days.length && days[i] < days[index] + 30) {
            i++;
        }

        int cost30DayPass = costs[2] + dfs(days, costs, i, dp);


        int result = Math.min(cost1DayPass, Math.min(cost7DayPass, cost30DayPass));

        dp[index] = result;

        return result;
    }
}
