class Solution {
public:
    int mincostTickets(vector<int>& days, vector<int>& costs, int ind=0, int d=0) {
        int cost=INT_MAX;
        static int arr[3]={1,7,30};
        if(ind>=days.size())
            return 0;
        for(int i=0;i<costs.size();i++)
        {
            if(d<days[ind])
                cost = min(costs[i]+mincostTickets(days, costs, ind+1, d+arr[i]), cost);
            else
                cost = min(cost, mincostTickets(days, costs, ind+1, d));
        }
        return cost;
