class Solution {
public:
    int mincostTickets(vector<int>& days, vector<int>& costs) {
        int m = days.size(), n = costs.size(), mx = *max_element(days.begin(),days.end());        
        vector<int> dp(mx+1,INT_MAX); // dp[i] is the minimum cost required upto ith day
        dp[0] = 0;
        unordered_set<int> st(days.begin(),days.end()); // To efficently check whether the day is a travel day or not
        for(int i = 1 ; i <= mx ; ++i) // Iterating over the maximum day in days array
        {
            if(st.count(i) == 0) // If there is no travel on that day then the cost is equal to the cost upto the previous day
                dp[i] = dp[i-1];
            else
            {
                dp[i] = min(dp[i],dp[i-1] + costs[0]); // Taking the minimum of the cost upto ith day and the minimum upto (i-1)th day and cost of 1 day ticket
                if(i - 7 >= 0)
                    dp[i] = min(dp[i],dp[i-7] + costs[1]); // Taking the minimum of the cost upto ith day and the minimum upto (i-7)th day and cost of 7 days ticket
                else 
                    dp[i] = min(dp[i],costs[1]); // Considering the case where cost of 7 days ticket is lower 
                if(i - 30 >= 0)
                    dp[i] = min(dp[i],dp[i-30] + costs[2]); // Taking the minimum of the cost upto ith day and the minimum upto (i-30)th day and cost of 30 days ticket
                else 
                    dp[i] = min(dp[i],costs[2]); // Considering the case where cost of 30 days ticket is lower
            }
        }
        return dp[mx]; // dp[last day] stores the minimum cost upto the last day in days array
    }
};
