class Solution {
public:
    int mincostTickets(vector<int>& days, vector<int>& costs) {
        int n = days.size();
        vector<vector<int>> dp(n + 1, vector<int> (1001, 0));

        for(int i = n - 1; i >= 0; i--){
            for(int lastValidDay = days[n-1]; lastValidDay >= -1; lastValidDay--){
                if(days[i] <= lastValidDay){
                    dp[i][lastValidDay + 1] = dp[i + 1][lastValidDay + 1]; 
                }else{
                    int oneDayPass = costs[0] + dp[i + 1][days[i] + 1];
                    int weekPass = costs[1] + dp[i + 1][days[i] + 6 + 1];
                    int monthPass = costs[2] + dp[i + 1][days[i] + 29 + 1];

                    dp[i][lastValidDay + 1] = min(oneDayPass, min(weekPass, monthPass));
                }
            }
        }
        return dp[0][0];
    }
};
