class Solution {
    public int mincostTickets(int[] days, int[] costs) {

        int dp[] = new int[days.length + 1];
        Arrays.fill(dp, -1);
        return f(days, costs, 0, dp);
    }
    int f (int days[], int cost[], int i, int dp[]) {
        if(i >= days.length)
            return 0;

        if(dp[i] != -1)
            return dp[i];
        //day 1
        int option1 = cost[0] + f(days, cost, i + 1, dp);

        int j;
        //day 7
        for(j = i; j < days.length && days[j] < days[i] + 7; j++);
        int option2 = cost[1] + f(days, cost, j, dp);

        //day 30
        //day 7

        for(j = i; j < days.length && days[j] < days[i] + 30; j++);
        int option3 = cost[2] + f(days, cost, j, dp);

        return dp[i] = Math.min(option1, Math.min(option2, option3));
    }
}
