        return mincostTickets(days, costs, 0, 0);
    }
    private int mincostTickets(int[] days, int[] costs, int s, int cost){
        if(s >= days.length) return cost;
        int minCost = Integer.MAX_VALUE;
        for(int x = 0; x<3; x++){
            if(x == 0){
                minCost = Math.min(minCost, mincostTickets(days, costs, s+1, cost+costs[0]));
            } else if(x == 1){
                int nextDay = days[s]+7-1;
                for(int i = s+1; i<=days.length; i++){
                    if(i == days.length){
                        minCost = Math.min(minCost, cost+costs[1]);
                    } else if(days[i] > nextDay){
                        minCost = Math.min(minCost, mincostTickets(days, costs, i, cost+costs[1]));
                        break;
                    }
                }
            } else {
                int nextDay = days[s]+30-1;
                for(int i = s+1; i<=days.length; i++){
                    if(i == days.length){
                        minCost = Math.min(minCost, cost+costs[2]);
                    } else if(days[i] > nextDay){
                        minCost = Math.min(minCost, mincostTickets(days, costs, i, cost+costs[2]));
                        break;
                    }
                }
            }
        }
        return minCost;
    }
}

