    int solve(int n, int[] days, int[] costs)
    {
        Queue<int[]> week = new LinkedList<>();
        Queue<int[]> month = new LinkedList<>();

        int ans= 0;

        for (int t : days) {
            while (!week .isEmpty() && week.peek()[0] + 7 <= t) {
                week.poll();
            }
            while (!month.isEmpty() && month.peek()[0] + 30 <= t) {
                month.poll();
            }
            week.offer(new int[]{t, ans + costs[1]});
            month.offer(new int[]{t, ans + costs[2]});
            ans= Math.min(ans + costs[0], Math.min(week.peek()[1], month.peek()[1]));
        }

        return ans;
