class Solution {
    public int mincostTickets(int[] days, int[] costs) {
        boolean[] isPresent = new boolean[days[days.length-1]+1];
        for(int x:days){
            isPresent[x]=true;
        }
        Integer[] dp = new Integer[isPresent.length];
        return solve(isPresent,days[0],costs,dp);
