    int f(vector<int> &nums,int ind,vector<int> &cost,int n){

        if(ind>=n) return 0;
        if(dp[ind]!=-1) return dp[ind];

        int way1 = cost[0] + f(nums,ind+1,cost,n);
        int tempInd = ind;
        while(tempInd<n && nums[tempInd]-nums[ind]<7){
            tempInd++;
        }
        int way2 = cost[1] + f(nums,tempInd,cost,n);
