class Solution {
public:
    vector<int> dp;

    int getNextDay(vector<int>& days, int dayIdx, int numDays) {
        int lastDay = days[dayIdx] + numDays;
        auto it = lower_bound(begin(days)+dayIdx, end(days), lastDay);
        if(it == days.end()) return days.size();
        int newDayIdx = it - days.begin();
        return newDayIdx;
    }

    int getMinCost(vector<int>& days, vector<int>& costs, int dayIdx) {
        if (dayIdx == days.size())
            return 0;
        if (dp[dayIdx] != -1)
            return dp[dayIdx];
        // Assume on a given day we bought a particular ticket
        // then recurse on remaining days function getNextDay tells till what time
        // current ticket is valid
        int totalDayCost = costs[0] + getMinCost(days,costs,getNextDay(days,dayIdx,1));
        int totalWeekCost = costs[1] + getMinCost(days,costs,getNextDay(days,dayIdx,7));
        int totalMonthCost = costs[2] + getMinCost(days,costs,getNextDay(days,dayIdx,30));
        dp[dayIdx]  = min({totalDayCost,totalWeekCost,totalMonthCost});
        return dp[dayIdx];
    }
    int mincostTickets(vector<int>& days, vector<int>& costs) {
        dp = vector<int>(days.size(),-1);
        return getMinCost(days,costs,0);
    }
};

